declare module '*.less' {
  const content: any;
  export default content;
}
declare module '*.css' {
  const content: any;
  export default content;
}

declare module '*.js' {
  const content: any;
  export default content;
}

declare module '*.json' {
  const content: any;
  export default content;
}

declare module '*.svg' {
  const content: any;
  export default content;
}

// tslint:disable-next-line:interface-name
interface NodeModule {
  hot: any;
}
